mapper.select:
select id, author_id, conversation_id, in_reply_to_user_id, create_time, `text`, `type`, referenced_chirper_id, media_keys, view_count, reply_count, like_count, quote_count, forward_count, `status` from tb_chirper where match(text) against(#{keyword}) and status ='${@com.zyq.chirp.chirperserver.domain.enums.ChirperStatus@ACTIVE.getStatus()}'
and media_keys is not null
order by create_time desc limit #{offset},#{size}

select id,author_id,referenced_chirper_id from tb_chirper where type=#{type}
and status='${@com.zyq.chirp.chirperserver.domain.enums.ChirperStatus@ACTIVE.getStatus()}'
and (referenced_chirper_id,author_id)
in

    #{item.referencedChirperId},
    #{item.authorId}
