mapper.insert:
replace into tb_chirper_like(chirper_id, user_id, create_time) select temp1.c_id,temp1.u_id,temp1.c_time from (
select #{item.chirperId} as c_id,#{item.userId} as u_id,#{item.createTime} as c_time
)as temp1 join tb_chirper tc on tc.id=temp1.c_id join tb_user tu on tu.id=temp1.u_id

mapper.delete:
delete from tb_chirper_like where (chirper_id, user_id) in (
(#{item.chirperId}, #{item.userId})
)

mapper.update:
update tb_chirper
set like_count=like_count + #{delta}
where id = #{chirpreId}
